import { userRouter } from 'next/router';
import Layout from '../layouts/Layout';

const Page = () => {
  const router = useRouter();

  return (
    <Layout>
      <h1>{ router.query.title }</h1>
      <p>This is the blog post content.</p>
    </Layout>
  );
}

export default Page;
